async function commitRename() {
  if (!editing.active || editing.index < 0 || !results[editing.index]) {
    return;
  }

  const current = results[editing.index];
  const nextName = editing.draftName;

  try {
    await invoke('rename', {
      path: current.path,
      newName: nextName
    });
    cancelRename();
    await runSearch();
  } catch (err) {
    showToast(`Failed to rename: ${String(err)}`);
    await tick();
    renameInputEl?.focus();
  }
}

