   147→  function bytesToBase64(bytes) {
   148→    let binary = '';
   149→    const chunk = 0x8000;
   150→    for (let i = 0; i < bytes.length; i += chunk) {
   151→      const part = bytes.subarray(i, i + chunk);
   152→      binary += String.fromCharCode(...part);
   153→    }
   154→    return btoa(binary);
   155→  }
   156→
   157→  function highlightSegments(name, q) {
   158→    q = (q || '').trim();
   159→    if (!q) return [{ text: name, hl: false }];
   160→
   161→    // Reset cache when query changes
   162→    if (q !== highlightCacheQuery) {
   163→      highlightCache = new Map();
   164→      highlightCacheQuery = q;
   165→    }
   166→
   167→    // Check cache
   168→    const cached = highlightCache.get(name);
   169→    if (cached) return cached;
   170→
   171→    if (q.includes('/')) q = q.slice(q.lastIndexOf('/') + 1);
   172→    q = q.replace(/[*?]/g, '');
   173→    const terms = q.split(/\s+/).filter(Boolean);
   174→    if (terms.length === 0) return [{ text: name, hl: false }];
   175→
   176→    const lower = name.toLowerCase();
   177→    const marks = new Uint8Array(name.length);
   178→    for (const t of terms) {
   179→      const tl = t.toLowerCase();
   180→      let idx = lower.indexOf(tl);
   181→      while (idx !== -1) {
   182→        for (let i = idx; i < idx + tl.length; i++) marks[i] = 1;
   183→        idx = lower.indexOf(tl, idx + 1);
   184→      }
   185→    }
   186→
   187→    const segs = [];
   188→    let i = 0;
   189→    while (i < name.length) {
   190→      const m = marks[i];
   191→      let j = i + 1;
   192→      while (j < name.length && marks[j] === m) j++;
   193→      segs.push({ text: name.slice(i, j), hl: m === 1 });
   194→      i = j;
   195→    }
   196→
   197→    highlightCache.set(name, segs);
   198→    return segs;
   199→  }
   200→
   201→  const perFileIconExts = new Set(['exe', 'lnk', 'ico', 'url', 'scr', 'appx']);
   202→
   203→  function iconKey(entry) {
   204→    if (entry.isDir) return '__folder__';
   205→    const ext = (entry.ext || '').toLowerCase();
   206→    if (perFileIconExts.has(ext)) return entry.path;
   207→    return ext || '__file__';
   208→  }
   209→
   210→  function iconFor(entry) {
   211→    return iconCache.get(iconKey(entry)) || (entry.isDir ? folderFallbackIcon : fileFallbackIcon);
   212→  }
   213→
   214→  async function ensureIcon(entry) {
   215→    const key = iconKey(entry);
   216→    if (iconCache.has(key) || iconLoading.has(key)) {
   217→      return;
   218→    }
   219→
   220→    iconLoading.add(key);
   221→    try {
   222→      const bytes = await invoke('get_file_icon', {
   223→        path: entry.path,
   224→        ext: entry.isDir ? 'folder' : entry.ext || '',
   225→      });
   226→      if (Array.isArray(bytes) && bytes.length > 0) {
   227→        const image = `data:image/png;base64,${bytesToBase64(Uint8Array.from(bytes))}`;
   228→        iconCache.set(key, image);
   229→      } else {
   230→        iconCache.set(key, entry.isDir ? folderFallbackIcon : fileFallbackIcon);
   231→      }
   232→    } catch {
   233→      iconCache.set(key, entry.isDir ? folderFallbackIcon : fileFallbackIcon);
   234→    } finally {
   235→      iconLoading.delete(key);
   236→    }
   237→  }
   238→
   239→  function syncColumnWidthsToContainer() {
   240→    const width = tableContainer?.clientWidth || tableAreaEl?.clientWidth;
   241→    if (!width || lastTableWidth !== 0) {
   242→      return;
   243→    }
   244→
   245→    const saved = loadColWidths();
   246→    if (saved) {
   247→      const total = columnKeys.reduce((s, k) => s + saved[k], 0);
   248→      if (Math.abs(total - width) > 2) {
   249→        const scale = (width - saved.modified) / (total - saved.modified);
   250→        let scaledName = Math.max(minColumnWidth.name, Math.round(saved.name * scale));
   251→        let scaledSize = Math.max(minColumnWidth.size, Math.round(saved.size * scale));
   252→        let rawPath = width - saved.modified - scaledName - scaledSize;
   253→        if (rawPath < minColumnWidth.path) {
   254→          const shortfall = minColumnWidth.path - rawPath;
   255→          const nameReduction = Math.min(shortfall, scaledName - minColumnWidth.name);
   256→          scaledName -= nameReduction;
   257→          const remaining = shortfall - nameReduction;
   258→          if (remaining > 0) {
   259→            scaledSize -= Math.min(remaining, scaledSize - minColumnWidth.size);
   260→          }
   261→          rawPath = width - saved.modified - scaledName - scaledSize;
   262→        }
   263→        const scaledPath = Math.max(minColumnWidth.path, rawPath);
   264→        colWidths = { name: scaledName, path: scaledPath, size: scaledSize, modified: saved.modified };
   265→      } else {
   266→        colWidths = saved;
   267→      }
   268→    } else {
   269→      const fixedModified = minColumnWidth.modified;
   270→      const rest = width - fixedModified;
   271→      const ratioSum = defaultColumnRatios.name + defaultColumnRatios.path + defaultColumnRatios.size;
   272→      colWidths = {
   273→        name: Math.max(minColumnWidth.name, Math.round(rest * defaultColumnRatios.name / ratioSum)),
   274→        path: Math.max(minColumnWidth.path, Math.round(rest * defaultColumnRatios.path / ratioSum)),
   275→        size: Math.max(minColumnWidth.size, Math.round(rest * defaultColumnRatios.size / ratioSum)),
   276→        modified: fixedModified
   277→      };
   278→    }
   279→    lastTableWidth = width;
   280→  }
   281→
   282→  function loadColWidths() {
   283→    try {
   284→      const raw = localStorage.getItem(COL_WIDTHS_KEY);
   285→      if (!raw) return null;
   286→      const parsed = JSON.parse(raw);
   287→      for (const key of columnKeys) {
   288→        if (typeof parsed[key] !== 'number' || parsed[key] < (minColumnWidth[key] || 0)) return null;
   289→      }
   290→      return parsed;
   291→    } catch {
   292→      return null;
   293→    }
   294→  }
   295→
   296→  function saveColWidths() {
   297→    localStorage.setItem(COL_WIDTHS_KEY, JSON.stringify(colWidths));
   298→  }
   299→
   300→  function updateViewportHeight() {
   301→    viewportHeight = tableContainer?.clientHeight || 520;
   302→    syncColumnWidthsToContainer();
   303→  }
   304→
   305→  function scheduleViewportHeightUpdate() {
   306→    if (viewportResizeRaf !== null) {
   307→      return;
   308→    }
   309→    viewportResizeRaf = requestAnimationFrame(() => {
   310→      viewportResizeRaf = null;
   311→      updateViewportHeight();
   312→    });
   313→  }
   314→
   315→  function startColumnResize(event, leftKey) {
   316→    event.preventDefault();
   317→    event.stopPropagation();
   318→
   319→    const index = columnKeys.indexOf(leftKey);
   320→    if (index < 0 || index >= columnKeys.length - 1) {
   321→      return;
   322→    }
   323→
   324→    const startX = event.clientX;
   325→    const startLeft = colWidths[leftKey];
   326→    const rightKey = columnKeys[index + 1];
   327→    const startRight = colWidths[rightKey];
   328→
   329→    resizeCleanup?.();
   330→    resizingColumn = leftKey;
   331→
   332→    const onMove = (moveEvent) => {
   333→      const delta = Math.round(moveEvent.clientX - startX);
   334→      if (leftKey === 'name') {
   335→        // Two-column trade: name and path swap space, each respects its minimum
   336→        const nextLeft = Math.max(
   337→          minColumnWidth[leftKey],
   338→          Math.min(startLeft + delta, startLeft + startRight - minColumnWidth[rightKey])
   339→        );
   340→        const nextRight = startLeft + startRight - nextLeft;
   341→        colWidths = { ...colWidths, [leftKey]: nextLeft, [rightKey]: nextRight };
   342→      } else {
   343→        // path splitter: path grows unconstrained (allows horizontal scroll on right drag)
   344→        const nextLeft = Math.max(minColumnWidth[leftKey], startLeft + delta);
   345→        colWidths = { ...colWidths, [leftKey]: nextLeft };
   346→      }
   347→    };
   348→
   349→    const onUp = () => {
   350→      cleanup();
   351→    };
   352→
   353→    const cleanup = () => {
   354→      window.removeEventListener('mousemove', onMove);
   355→      window.removeEventListener('mouseup', onUp);
   356→      document.body.style.cursor = '';
   357→      document.body.style.userSelect = '';
   358→      resizingColumn = '';
   359→      saveColWidths();
   360→      if (resizeCleanup === cleanup) {
   361→        resizeCleanup = null;
   362→      }
   363→    };
   364→
   365→    resizeCleanup = cleanup;
   366→    document.body.style.cursor = 'col-resize';
   367→    document.body.style.userSelect = 'none';
   368→    window.addEventListener('mousemove', onMove);
   369→    window.addEventListener('mouseup', onUp);
   370→  }
   371→
   372→  function selectedPaths() {
   373→    const sorted = [...selectedIndices].sort((a, b) => a - b);
   374→    return sorted.map((idx) => results[idx]).filter(Boolean).map((entry) => entry.path);
   375→  }
   376→
   377→  function clearSelection() {
   378→    selectedIndices = new Set();
   379→    selectionAnchor = -1;
   380→    lastSelectedIndex = -1;
   381→  }
   382→
   383→  function selectSingle(index) {
   384→    selectedIndices = new Set([index]);
   385→    selectionAnchor = index;
   386→    lastSelectedIndex = index;
   387→  }
   388→
   389→  function selectToggle(index) {
   390→    const next = new Set(selectedIndices);
   391→    if (next.has(index)) {
   392→      next.delete(index);
   393→    } else {
   394→      next.add(index);
   395→    }
   396→    selectedIndices = next;
   397→    selectionAnchor = index;
   398→    lastSelectedIndex = index;
   399→  }
   400→
   401→  function selectRange(index) {
   402→    const anchor = selectionAnchor >= 0 ? selectionAnchor : index;
   403→    const [from, to] = [Math.min(anchor, index), Math.max(anchor, index)];
   404→    const next = new Set();
   405→    for (let i = from; i <= to; i += 1) {
   406→      next.add(i);
   407→    }
   408→    selectedIndices = next;
   409→    lastSelectedIndex = index;
   410→  }
   411→
   412→  function primaryEntry() {
   413→    const idx = lastSelectedIndex >= 0 ? lastSelectedIndex : -1;
   414→    return idx >= 0 ? results[idx] : null;
   415→  }
   416→
   417→  async function refreshStatus() {
   418→    if (statusRefreshInFlight) {
   419→      return;
   420→    }
   421→    statusRefreshInFlight = true;
   422→    try {
   423→      const status = await invoke('get_index_status');
   424→      const prevState = indexStatus.state;
   425→      indexStatus = {
   426→        state: status.state,
   427→        entriesCount: status.entriesCount,
   428→        lastUpdated: status.lastUpdated,
   429→        permissionErrors: status.permissionErrors ?? 0,
   430→        message: status.message
   431→      };
   432→      if (status.state === 'Indexing' && prevState !== 'Indexing') {
   433→        startElapsedTimer();
   434→      } else if (status.state !== 'Indexing' && prevState === 'Indexing') {
   435→        stopElapsedTimer();
   436→      }
   437→      if (typeof status.scanned === 'number') {
   438→        scanned = status.scanned;
   439→      }
   440→      if (status.state === 'Ready' && prevState !== 'Ready') {
   441→        scheduleSearch();
   442→      }
   443→    } catch (err) {
   444→      showToast(`Failed to get status: ${String(err)}`);
   445→    } finally {
   446→      statusRefreshInFlight = false;
   447→    }
   448→  }
   449→
   450→  function scheduleSearch(preserveScroll = false) {
   451→    searchGeneration += 1;
   452→    const scheduledGen = searchGeneration;
   453→    clearTimeout(searchTimer);
   454→
   455→    const now = performance.now();
   456→    if (now - lastSearchFiredAt >= 200) {
   457→      // Leading edge: fire immediately if enough time has passed
   458→      lastSearchFiredAt = now;
   459→      void runSearch(preserveScroll);
   460→    } else {
   461→      // Trailing edge: debounce
   462→      searchTimer = setTimeout(() => {
   463→        if (scheduledGen !== searchGeneration) {
   464→          return;
   465→        }
   466→        lastSearchFiredAt = performance.now();
   467→        void runSearch(preserveScroll);
   468→      }, 200);
   469→    }
   470→  }
   471→
   472→  async function runSearch(preserveScroll = false) {
   473→    searchGeneration += 1;
   474→    const gen = searchGeneration;
   475→    const searchQuery = query;
   476→    const searchSortBy = sortBy;
   477→    const searchSortDir = sortDir;
   478→    const startedAt = performance.now();
   479→    try {
   480→      const keepPaths = new Set(selectedPaths());
   481→      const next = await invoke('search', {
   482→        query: searchQuery,
   483→        limit: PAGE_SIZE,
   484→        offset: 0,
   485→        sortBy: searchSortBy,
   486→        sortDir: searchSortDir
   487→      });
   488→
   489→      if (gen !== searchGeneration) return;
   490→
   491→      dbLatencyMs = Math.round(performance.now() - startedAt);
   492→      dbLastQuery = searchQuery;
   493→      const entries = Array.isArray(next.entries) ? next.entries : [];
   494→      searchModeLabel = next.modeLabel || '';
   495→      results = entries;
   496→      if (next.totalCount > 0) {
   497→        totalResults = next.totalCount;
   498→        totalResultsKnown = true;
   499→      } else {
   500→        totalResults = entries.length;
   501→        totalResultsKnown = false;
   502→      }
   503→      hasMore = totalResultsKnown ? results.length < totalResults : entries.length >= PAGE_SIZE;
   504→      if (!preserveScroll && tableContainer) tableContainer.scrollTop = 0;
   505→
   506→      const restored = new Set();
   507→      for (let i = 0; i < results.length; i += 1) {
   508→        if (keepPaths.has(results[i].path)) {
   509→          restored.add(i);
   510→        }
   511→      }
   512→      selectedIndices = restored;
   513→
   514→      updateViewportHeight();
   515→    } catch (err) {
   516→      if (gen !== searchGeneration) return;
   517→      showToast(`Search failed: ${String(err)}`);
   518→    }
   519→  }
   520→
   521→  async function loadMore() {
   522→    if (!hasMore || loadingMore) return;
   523→    const gen = searchGeneration;
   524→    loadingMore = true;
   525→    try {
   526→      const batch = await invoke('search', {
   527→        query,
   528→        limit: PAGE_SIZE,
   529→        offset: results.length,
   530→        sortBy: sortBy,
   531→        sortDir: sortDir
   532→      });
   533→      if (gen !== searchGeneration) return;
   534→      const arr = Array.isArray(batch.entries) ? batch.entries : [];
   535→      if (arr.length > 0) results = [...results, ...arr];
   536→      hasMore = totalResultsKnown ? results.length < totalResults : arr.length >= PAGE_SIZE;
   537→    } catch (err) {
   538→      showToast(`Failed to load more: ${String(err)}`);
   539→    } finally {
   540→      loadingMore = false;
   541→    }
   542→  }
   543→
   544→  function moveSelection(delta, withRange = false) {
   545→    if (results.length === 0) {
   546→      return;
   547→    }
   548→
   549→    const current = lastSelectedIndex >= 0 ? lastSelectedIndex : 0;
   550→    const next = Math.max(0, Math.min(results.length - 1, current + delta));
   551→
   552→    if (withRange) {
   553→      selectRange(next);
   554→    } else {
   555→      selectSingle(next);
   556→    }
   557→
   558→    const top = next * rowHeight;
   559→    const bottom = top + rowHeight;
   560→    if (top < scrollTop) {
   561→      tableContainer.scrollTop = top;
   562→    } else if (bottom > scrollTop + viewportHeight) {
   563→      tableContainer.scrollTop = bottom - viewportHeight;
   564→    }
   565→  }
   566→
   567→  function handleHeaderSort(column) {
   568→    if (sortBy === column) {
   569→      sortDir = sortDir === 'asc' ? 'desc' : 'asc';
   570→    } else {
   571→      sortBy = column;
   572→      sortDir = 'asc';
   573→    }
   574→    if (tableContainer) tableContainer.scrollTop = 0;
   575→    void runSearch();
   576→  }
   577→
   578→  function handleRowClick(event, index) {
   579→    contextMenu.visible = false;
   580→
   581→    if (event.shiftKey) {
   582→      selectRange(index);
   583→      return;
   584→    }
   585→
   586→    if (event.metaKey || event.ctrlKey) {
   587→      selectToggle(index);
   588→      return;
   589→    }
   590→
   591→    selectSingle(index);
   592→  }
   593→
   594→  function handleRowContextMenu(event, index) {
   595→    event.preventDefault();
   596→
   597→    if (!selectedIndices.has(index)) {
   598→      selectSingle(index);
   599→    }
   600→
   601→    if (platform === 'windows' || platform === 'macos') {
   602→      contextMenu.visible = false;
   603→      const paths = selectedPaths();
   604→      if (paths.length > 0) {
   605→        invoke('show_context_menu', {
   606→          paths,
   607→          x: event.clientX,
   608→          y: event.clientY,
   609→          singleSelection: selectedIndices.size === 1
   610→        });
   611→      }
   612→      return;
   613→    }
   614→
   615→    contextMenu = {
