  1254→          state: 'Indexing'
  1255→        };
  1256→      }
  1257→    });
  1258→    flog(`[startup/fe] +${ms()}ms listen(index_progress) registered`);
  1259→
  1260→    const unlistenState = await listen('index_state', (event) => {
  1261→      flog(`[startup/fe] index_state event received: ${event.payload.state} at +${ms()}ms`);
  1262→      const prevState = indexStatus.state;
  1263→      indexStatus = {
  1264→        ...indexStatus,
  1265→        state: event.payload.state,
  1266→        message: event.payload.message ?? null,
  1267→        isCatchup: event.payload.isCatchup ?? false
  1268→      };
  1269→
  1270→      if (event.payload.state === 'Indexing' && prevState !== 'Indexing') {
  1271→        if (prevState === 'Ready') {
  1272→          lastReadyCount = indexStatus.entriesCount;
  1273→        }
  1274→        startElapsedTimer();
  1275→      } else if (event.payload.state !== 'Indexing' && prevState === 'Indexing') {
  1276→        stopElapsedTimer();
  1277→      }
  1278→
  1279→      if (event.payload.state === 'Ready') {
  1280→        scheduleSearch();
  1281→      }
  1282→    });
  1283→    flog(`[startup/fe] +${ms()}ms listen(index_state) registered`);
  1284→
  1285→    const unlistenUpdated = await listen('index_updated', (event) => {
  1286→      indexStatus = {
  1287→        ...indexStatus,
  1288→        entriesCount: event.payload.entriesCount,
  1289→        lastUpdated: event.payload.lastUpdated,
  1290→        permissionErrors: event.payload.permissionErrors ?? indexStatus.permissionErrors
  1291→      };
  1292→
  1293→      scheduleSearch(true);
  1294→    });
  1295→
  1296→    const unlistenFocus = await listen('focus_search', () => {
  1297→      void focusSearch();
  1298→    });
  1299→
  1300→    const unlistenCtxMenuAction = await listen('context_menu_action', (event) => {
  1301→      switch (event.payload) {
  1302→        case 'open': void openSelected(); break;
  1303→        case 'quick_look': {
  1304→          const e = primaryEntry();
  1305→          if (e) void invoke('quick_look', { path: e.path });
  1306→          break;
  1307→        }
  1308→        case 'open_with': void openWithFallback(); break;
  1309→        case 'reveal': void revealSelected(); break;
  1310→        case 'copy_files': void copyFiles(); break;
  1311→        case 'copy_path': void copySelectedPaths(); break;
  1312→        case 'trash': void trashSelected(); break;
  1313→        case 'rename': void startRename(); break;
  1314→      }
  1315→    });
  1316→
  1317→    unlistenFns = [unlistenProgress, unlistenState, unlistenUpdated, unlistenFocus, unlistenCtxMenuAction];
  1318→    flog(`[startup/fe] +${ms()}ms all listeners registered`);
  1319→
  1320→    // Fetch backend state IMMEDIATELY after listeners are registered.
  1321→    // Events emitted before listener registration are lost, so this
  1322→    // is the only reliable way to get the current state.
  1323→    await refreshStatus();
  1324→    flog(`[startup/fe] +${ms()}ms refreshStatus done (state=${indexStatus.state})`);
  1325→
  1326→    // Show Svelte UI: remove skeleton, focus search input.
  1327→    await tick();
  1328→    document.getElementById('skeleton')?.remove();
  1329→    await focusSearch();
  1330→    flog(`[startup/fe] +${ms()}ms WINDOW VISIBLE — skeleton removed`);
  1331→
  1332→    try {
  1333→      homePrefix = await invoke('get_home_dir');
  1334→    } catch {
  1335→      homePrefix = '';
  1336→    }
  1337→    flog(`[startup/fe] +${ms()}ms get_home_dir done`);
  1338→
  1339→    if (!localStorage.getItem('everything-fda-notice-v1')) {
  1340→      showToast('macOS Full Disk Access permission may be required for full disk search.');
  1341→      localStorage.setItem('everything-fda-notice-v1', '1');
  1342→    }
  1343→    flog(`[startup/fe] +${ms()}ms calling runSearch...`);
  1344→    await runSearch();
  1345→    flog(`[startup/fe] +${ms()}ms runSearch done`);
  1346→
  1347→    if (typeof ResizeObserver !== 'undefined') {
  1348→      tableResizeObserver = new ResizeObserver(() => {
  1349→        scheduleViewportHeightUpdate();
  1350→      });
  1351→      if (tableContainer) {
  1352→        tableResizeObserver.observe(tableContainer);
  1353→      } else if (tableAreaEl) {
  1354→        tableResizeObserver.observe(tableAreaEl);
  1355→      }
  1356→    }
  1357→
  1358→    window.addEventListener('resize', scheduleViewportHeightUpdate);
  1359→    window.addEventListener('click', onGlobalClick);
  1360→    statusRefreshTimer = setInterval(() => {
  1361→      if (indexStatus.state === 'Indexing') {
  1362→        void refreshStatus();
  1363→      }
  1364→    }, 1000);
  1365→    console.log(`[startup/fe] +${ms()}ms onMount complete`);
  1366→  });
  1367→
  1368→  onDestroy(async () => {
  1369→    clearTimeout(searchTimer);
  1370→    clearTimeout(toastTimer);
  1371→    clearInterval(statusRefreshTimer);
  1372→    clearInterval(elapsedTimer);
  1373→    resizeCleanup?.();
  1374→
  1375→    for (const unlisten of unlistenFns) {
  1376→      unlisten();
  1377→    }
  1378→
  1379→    tableResizeObserver?.disconnect();
  1380→    tableResizeObserver = null;
  1381→    if (viewportResizeRaf !== null) {
  1382→      cancelAnimationFrame(viewportResizeRaf);
  1383→      viewportResizeRaf = null;
  1384→    }
  1385→    window.removeEventListener('resize', scheduleViewportHeightUpdate);
  1386→    window.removeEventListener('click', onGlobalClick);
  1387→  });
