async function startRename() {
  if (isMultiSelected()) {
    return;
  }

  const idx = lastSelectedIndex;
  if (idx < 0 || !results[idx]) {
    return;
  }

  const entry = results[idx];

  // Calculate overlay position from the row's name cell text span
  const rowDomIndex = idx - startIndex;
  const rowEls = tableContainer?.querySelectorAll('.row');
  const rowEl = rowEls?.[rowDomIndex];
  const nameCell = rowEl?.querySelector('.cell.name');

  if (nameCell) {
    const cellRect = nameCell.getBoundingClientRect();
    const ellipsis = nameCell.querySelector('.ellipsis');
    const textLeft = ellipsis ? ellipsis.getBoundingClientRect().left : cellRect.left + 38;
    // Offset left by border(1.5) + padding-left(4) so text inside textarea aligns with row text
    const pad = 6;
    renameOverlay = {
      top: cellRect.top,
      left: textLeft - pad,
      width: cellRect.right - textLeft + pad - 4
    };
  }

  editing = {
    active: true,
    path: entry.path,
    index: idx,
    draftName: entry.name
  };

  await tick();

  if (renameInputEl) {
    autoResizeRenameInput();
    renameInputEl.focus();
    const extPos = !entry.isDir ? entry.name.lastIndexOf('.') : -1;
    const selectionEnd = extPos > 0 ? extPos : entry.name.length;
    renameInputEl.setSelectionRange(0, selectionEnd);
  }
}

