      contextMenu.visible = false;
      const paths = selectedPaths();
      if (paths.length > 0) {
        invoke('show_context_menu', {
          paths,
          x: event.clientX,
          y: event.clientY,
          singleSelection: selectedIndices.size === 1
        });
